sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

	// initialize the variables for the name of the what needed field and the field value
	var wn_name = 'u_what_needed';
	var whatneeded = g_form.getValue(wn_name);

	// clear all the choices of the what needed field
	g_form.clearOptions(wn_name);

	// add the appropriate options for the selected Request type
	switch(newValue) {
		case 'hr':
			g_form.addOption(wn_name, 'hr1', 'Human Resources 1');
			g_form.addOption(wn_name, 'hr2', 'Human Resources 2');
			g_form.addOption(wn_name, 'other', 'Other');
			break;
		case 'facilities':
			g_form.addOption(wn_name, 'facilities1', 'Facilities 1');
			g_form.addOption(wn_name, 'facilities2', 'Facilities 2');
			g_form.addOption(wn_name, 'other', 'Other');
			break;
		case 'legal':
			g_form.addOption(wn_name, 'legal1', 'Legal 1');
			g_form.addOption(wn_name, 'legal2', 'Legal 2');
			g_form.addOption(wn_name, 'other', 'Other');
	}

	// if the form is loading and it is not a new record, set the what needed field to the value from the record before it was loaded
	if(isLoading && !g_form.isNewRecord()){
		g_form.setValue(wn_name, whatneeded);
	}
}
