sys_script_client.script:
function onLoad() {
   // set the appropriate options for what needed field on load
	var requestType = g_form.getValue('u_request_type');
	var whatneeded = g_form.getValue('u_what_needed');
	var wn_name = 'u_what_needed';

	g_form.clearOptions(wn_name);

	switch(requestType) {
		case 'hr':
			g_form.addOption(wn_name, 'hr1', 'Human Resources 1');
			g_form.addOption(wn_name, 'hr2', 'Human Resources 2');
			g_form.addOption(wn_name, 'other', 'Other');
			break;
		case 'facilities':
			g_form.addOption(wn_name, 'facilities1', 'Facilities 1');
			g_form.addOption(wn_name, 'facilities2', 'Facilities 2');
			g_form.addOption(wn_name, 'other', 'Other');
			break;
		case 'legal':
			g_form.addOption(wn_name, 'legal1', 'Legal 1');
			g_form.addOption(wn_name, 'legal2', 'Legal 2');
			g_form.addOption(wn_name, 'other', 'Other');
	}

	if(!g_form.isNewRecord()) {
		g_form.setValue(wn_name, whatneeded);
	}
}
